import React, { useState } from "react";
import axios from "axios";

function App() {
    const [riskFactors, setRiskFactors] = useState("");
    const [result, setResult] = useState(null);

    const handleSubmit = async () => {
        const factors = riskFactors.split(",").map((factor) => ({
            name: factor.trim(),
            severity: Math.floor(Math.random() * 10) + 1, // Simulate severity
        }));
        const response = await axios.post("http://127.0.0.1:5000/assess-risk", { risk_factors: factors });
        setResult(response.data);
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial" }}>
            <h1>Risk Assessment Framework</h1>
            <textarea
                placeholder="Enter risk factors, separated by commas"
                rows="5"
                cols="50"
                onChange={(e) => setRiskFactors(e.target.value)}
            />
            <br />
            <button onClick={handleSubmit}>Assess Risk</button>
            {result && (
                <div>
                    <h2>Assessment Results</h2>
                    <p>Risk Score: {result.risk_score}</p>
                    <p>Recommendations:</p>
                    <ul>
                        {result.recommendations.map((rec, index) => (
                            <li key={index}>{rec}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default App;
