import React, { useState } from "react";
import axios from "axios";
import { TextField, Button, Typography, Box, Paper, Select, MenuItem, Divider } from "@mui/material";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function App() {
  const [riskFactors, setRiskFactors] = useState("");
  const [result, setResult] = useState(null);
  const [complianceType, setComplianceType] = useState("ISO 27001");
  const [complianceResult, setComplianceResult] = useState(null);

  const [plainText, setPlainText] = useState("");
  const [encryptedText, setEncryptedText] = useState("");
  const [decryptedText, setDecryptedText] = useState("");

  const [scanTarget, setScanTarget] = useState("");
const [scanResults, setScanResults] = useState(null);

  // Handle Risk Assessment
  const handleRiskAssessment = async () => {
    const factors = riskFactors.split(",").map((factor) => ({
      name: factor.trim(),
      severity: Math.floor(Math.random() * 10) + 1, // Random severity for demo
    }));

    try {
      const response = await axios.post("http://127.0.0.1:5000/assess-risk", {
        risk_factors: factors,
      });
      setResult(response.data);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  // Handle Compliance Check
  const handleComplianceCheck = async () => {
    const factors = riskFactors.split(",").map((factor) => ({
      name: factor.trim(),
      severity: Math.floor(Math.random() * 10) + 1,
    }));

    try {
      const response = await axios.post("http://127.0.0.1:5000/check-compliance", {
        compliance_type: complianceType,
        risk_factors: factors,
      });
      setComplianceResult(response.data);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  // Handle Encryption
  const handleEncrypt = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:5000/encrypt", {
        text: plainText,
      });
      setEncryptedText(response.data.encrypted_text);
    } catch (error) {
      console.error("Error encrypting text:", error);
    }
  };

  // Handle Decryption
  const handleDecrypt = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:5000/decrypt", {
        encrypted_text: encryptedText,
      });
      setDecryptedText(response.data.decrypted_text);
    } catch (error) {
      console.error("Error decrypting text:", error);
    }
  };

  return (
    <Box sx={{ padding: "20px", fontFamily: "Arial" }}>
      <Typography variant="h4" gutterBottom>
        Risk Assessment Framework
      </Typography>
      <TextField
        label="Enter Risk Factors (comma-separated)"
        variant="outlined"
        fullWidth
        onChange={(e) => setRiskFactors(e.target.value)}
      />
      <Select
        value={complianceType}
        onChange={(e) => setComplianceType(e.target.value)}
        fullWidth
        sx={{ marginTop: "10px" }}
      >
        <MenuItem value="ISO 27001">ISO 27001</MenuItem>
        <MenuItem value="HIPAA">HIPAA</MenuItem>
        <MenuItem value="PCI DSS">PCI DSS</MenuItem>
      </Select>
      <Button
        variant="contained"
        color="primary"
        sx={{ marginTop: "10px" }}
        onClick={handleRiskAssessment}
      >
        Assess Risk
      </Button>
      <Button
        variant="outlined"
        color="secondary"
        sx={{ marginTop: "10px", marginLeft: "10px" }}
        onClick={handleComplianceCheck}
      >
        Check Compliance
      </Button>

      {result && (
        <Paper elevation={3} sx={{ marginTop: "20px", padding: "20px" }}>
          <Typography variant="h5">Assessment Results</Typography>
          <Typography>Risk Score: {result.risk_score}</Typography>
          <Typography>Recommendations:</Typography>
          <ul>
            {result.recommendations.map((rec, index) => (
              <li key={index}>{rec}</li>
            ))}
          </ul>
          <Bar
            data={{
              labels: result.recommendations.map((_, index) => `Risk ${index + 1}`),
              datasets: [
                {
                  label: "Severity Levels",
                  data: result.recommendations.map(() =>
                    Math.floor(Math.random() * 10) + 1
                  ), // Example severity data
                  backgroundColor: "rgba(75,192,192,0.6)",
                },
              ],
            }}
            options={{
              responsive: true,
              plugins: {
                legend: { display: true },
              },
            }}
          />
        </Paper>
      )}

      {complianceResult && (
        <Paper elevation={3} sx={{ marginTop: "20px", padding: "20px" }}>
          <Typography variant="h5">Compliance Results</Typography>
          <Typography>Compliance Type: {complianceResult.compliance_type}</Typography>
          <Typography>Non-Compliant Factors:</Typography>
          <ul>
            {complianceResult.non_compliant_factors.map((factor, index) => (
              <li key={index}>{factor}</li>
            ))}
          </ul>
        </Paper>
      )}

      <Divider sx={{ marginY: "20px" }} />
      <Typography variant="h4" gutterBottom>
        Real-Time Encryption/Decryption
      </Typography>
      <TextField
        label="Plain Text"
        variant="outlined"
        fullWidth
        sx={{ marginY: "10px" }}
        onChange={(e) => setPlainText(e.target.value)}
      />
      <Button
        variant="contained"
        color="primary"
        sx={{ marginRight: "10px" }}
        onClick={handleEncrypt}
      >
        Encrypt
      </Button>
      <Typography variant="body1" sx={{ marginTop: "10px" }}>
        Encrypted Text: {encryptedText}
      </Typography>

      <TextField
        label="Encrypted Text"
        variant="outlined"
        fullWidth
        sx={{ marginY: "10px" }}
        value={encryptedText}
        onChange={(e) => setEncryptedText(e.target.value)}
      />
      <Button variant="outlined" color="secondary" onClick={handleDecrypt}>
        Decrypt
      </Button>
      <Typography variant="body1" sx={{ marginTop: "10px" }}>
        Decrypted Text: {decryptedText}
      </Typography>
    </Box>
  );
}

export default App;
